import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string="";
  password: string="";
  isLogin: boolean=true;
  errorMessage: string="";

  constructor(private router:Rou)
}
<div class="container mt-4">
    <div class="card">
        <form>

            <div class="form-group">
              <label>Email address</label>
              <input type="email" [(ngModel)]="email" [ngModelOptions]="{standalone: true}" class="form-control" aria-describedby="emailHelp" placeholder="Enter email">
            </div>
            <br>
            <div class="form-group">
              <label>Password</label>
              <input type="password" [(ngModel)]="password" [ngModelOptions]="{standalone: true}" class="form-control" placeholder="Password">
            </div>
            <br>
            <button type="submit" (click)="login()" class="btn btn-primary">Submit</button>
          </form>
    </div>
</div>
