import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  username : string="";
  email : string="";
  password : string="";
  
  constructor(private router:Router, private http: HttpClient){

  }

  ngOnInit() : void{

  }
  register(){
    let bodyData ={
    "username" : this.username,
    "email" : this.email,
    "password" : this.password,
  };
  this.http.post("http://localhost:4000/user/save", bodyData).subscribe((resultData: any)=>{
    console.log(resultData);
    alert("User has been registered successfully.");
  });
  }
}
<div class="container mt-4">
    <div class="card">
        <form>
            <div class="form-group">
                <label>Username</label>
                <input type="text" [(ngModel)]="username" [ngModelOptions]="{standalone: true}" class="form-control" aria-describedby="emailHelp" placeholder="Enter username">
              </div>
              <br>
            <div class="form-group">
              <label>Email address</label>
              <input type="email" [(ngModel)]="email" [ngModelOptions]="{standalone: true}" class="form-control" aria-describedby="emailHelp" placeholder="Enter email">
            </div>
            <br>
            <div class="form-group">
              <label>Password</label>
              <input type="password" [(ngModel)]="password" [ngModelOptions]="{standalone: true}" class="form-control" placeholder="Password">
            </div>
            <br>
            <button type="submit" (click)="register()" class="btn btn-primary">Submit</button>
          </form>
    </div>
</div>
